import { useState } from "react";

const name = 'name';
const email = 'email';

const emailRegex = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g;

const SimpleInput = (props) => {

    const [inputsValue, setInputsValue] = useState({
        [name]: '',
        [email]: ''
    });
    const [haveInputsInvalid, setHaveInputsInvalid] = useState({
        [name]: false,
        [email]: false
    });
    const [haveInputsTouched, setHaveInputsTouched] = useState({
        [name]: false,
        [email]: false
    });

    const haveInputsUncorrectedFilled = {
        name: haveInputsInvalid.name && haveInputsTouched.name,
        email: haveInputsInvalid.email && haveInputsTouched.email
    };

    const onBlurHandler = (event) => {
        const inputName = event.target.name;
        const inputValue = event.target.value;

        if (inputName === name) {
            const hasNameUncorrect = !inputValue || inputValue.length <= 3;
            setHaveInputsInvalid(prevState => ({ ...prevState, [inputName]: hasNameUncorrect }))
        } else if (inputName === email) {
            const emailRegexpTest = inputValue.match(emailRegex);
            setHaveInputsInvalid(prevState => ({ ...prevState, [inputName]: !emailRegexpTest }))
        }
    };

    const onFocusHandler = (event) => {
        setHaveInputsTouched((prevState) => ({ ...prevState, [event.target.name]: true }));
    }

    const onChangeHandler = (event) => {
        setInputsValue((prevState) => ({ ...prevState, [event.target.name]: event.target.value }));
    };

    const onSubmitHandler = (e) => {
        e.preventDefault();

        console.log('haveInputsUncorrectedFilled', haveInputsUncorrectedFilled);

        setInputsValue({ name: '', email: '' });
    };

    return (
        <form onSubmit={onSubmitHandler}>
            <div className={haveInputsUncorrectedFilled.email ? 'form-control invalid' : 'form-control'}>
                <label htmlFor='name'>Your Email</label>
                <input
                    type='email'
                    name='email'
                    onChange={onChangeHandler}
                    onBlur={onBlurHandler}
                    onFocus={onFocusHandler}
                    value={inputsValue.email} />
                {haveInputsUncorrectedFilled.email ? <p className="error-text">Please use correct email address!</p> : null}
            </div>
            <div className={haveInputsUncorrectedFilled.name ? 'form-control invalid' : 'form-control'}>
                <label htmlFor='name'>Your Name</label>
                <input
                    type='text'
                    name='name'
                    onChange={onChangeHandler}
                    onBlur={onBlurHandler}
                    onFocus={onFocusHandler}
                    value={inputsValue.name} />
                {haveInputsUncorrectedFilled.name ? <p className="error-text">Please use user name with more then 3 symbols!</p> : null}
            </div >
            <div className="form-actions" >
                <button>Submit</button>
            </div >
        </form >
    );
};

export default SimpleInput;
